    //         method: 'GET',
    //         credentials: 'include',
    //     })
    //     .then((resp) => {
    //         return resp.json();
    //     })
    //     .then((data) => {
    //         console.log('Info Handler');
    //         console.log(data);
    //         fetchedContentInfo = data;
    //         // console.log(fetchedContentInfo);
    //         return data;
    //     })
    //     .catch((err) => {
    //         return console.error(err);
    //     })
    // }

    async function ratingsListHandler(props) {
        //console.log('Ratings Handler');
        //console.log(props);
        const array = [];
        //console.log(props.ratings.length);
        for (let i = (props.ratings.length - 1); i >= 0; i--) {
            fetchRatingsInfoHandler(props.ratings[i].content_id);
            console.log('inside ratingsListHandler:');
            console.log(fetchedContentInfo);
            array.push({
                id: i,
                name: fetchedContentInfo.name
            });
            console.log(array);
        }
        setRatingsList(array.map((array) => {
            return (
                <li key={array.id}>
                    {array.name}
                </li>
            );
        }))
    };

    return (
        <div>
            <h1>Ratings</h1>
            <div>
                <h2>Track ratings:</h2>
                {ratingsList}
            </div>
        </div>
    );
};

export default Ratings;
