import React, { useState, useEffect } from 'react';

const Ratings = () => {
    useEffect(() => {
        fetch('http://127.0.0.1:8000/ratings/list', {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            return ratingsListHandler(data);
        })
        .catch((err) => {
            return console.error(err);
        })
    }, []);

    // const [fetchedContentInfo, setFetchedContentInfo] = useState([]);
    let fetchedContentInfo = [];
    const [ratingsList, setRatingsList] = useState([]);

    const ratingsListHandler = (props) => {
        //console.log('Ratings Handler');
        //console.log(props);
        const array = [];
        //console.log(props.ratings.length);
        for (let i = (props.ratings.length - 1); i >= 0; i--) {
            fetch('http://127.0.0.1:8000/content/'+props.ratings[i].content_id, {
                method: 'GET',
                credentials: 'include',
            })
            .then((resp) => {
                return resp.json();
            })
            .then((data)=> {
                console.log(data);
                fetchedContentInfo.push({
                    id: i,
                    name: data.name,
                    uri: data.uri
                });
                return;
            })
            .catch((err) => {
                return console.error(err);
            })
            console.log(array);
        }
        // setRatingsList(array.map((array) => {
        //     console.log('Inside setRatingsList');
        //     return (
        //         <li key={array.id}>
        //             {array.name}
        //         </li>
        //     );
        // }
        // )) 
        console.log(ratingsList);
    }


    return (
        <div>
            <h1>Ratings</h1>
            <div>
                <h2>Track ratings:</h2>
                {ratingsList}
            </div>
        </div>
    );
};

export default Ratings;
