import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'

const RateTrack = () => {
    const { content_id } = useParams();
    useEffect(() => {
        fetch('http://127.0.0.1:8000/content/track/'+content_id, {
            method: 'GET',
            credentials: 'include',
          })
          .then((resp) => {
            return resp.json();
          })
          .then((data) => {
            return console.log(data);
          })
          .catch((err) => {
            return console.error(err);
          })

        fetch('http://127.0.0.1:8000/spotify/user/fetch_tracks?tracks='+content_id, {
            method: 'GET',
            credentials: 'include',
          })
          .then((resp) => {
            return resp.json();
          })
          .then((data) => {
            return trackInfoHandler(data);
          })
          .catch((err) => {
            return console.error(err);
          })
    }, []);

    const [trackInfo, setTrackInfo] = useState({
        name: '',
        artist: '',
        album: ''
    });
    const trackInfoHandler = (props) => {
        console.log(props);
        console.log(props.tracks[0].name);
        setTrackInfo({
            name: props.tracks[0],
            //artist: props[0].tracks[0].artist[0].name,
            //album: props[0].tracks[0].album.name
        });
    };
    return (
        <h1>Rate</h1>
    );
};

export default RateTrack;
