import React, { useState, useEffect } from 'react';

const Ratings = () => {
    useEffect(() => {
        fetch('http://127.0.0.1:8000/ratings/list', {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            return ratingsListHandler(data);
        })
        .catch((err) => {
            return console.error(err);
        })
    }, []);

    const fetchRatingsInfoHandler = (content_id) => {
        fetch('http://127.0.0.1:8000/content/'+content_id, {
            method: 'GET',
            credentials: 'include',
        })
        .then((resp) => {
            return resp.json();
        })
        .then((data) => {
            return (data);
        })
        .catch((err) => {
            return console.error(err);
        })
    }

    const ratingsListHandler = (props) => {
        console.log(props);
        const array = [];
        let ratings_info = [];
        for (let i = props.length; (i-1) > 0; i--) {
            ratings_info = fetchRatingsInfoHandler(i);
            console.log(ratings_info);
            array.push({
                name: ratings_info.name
            });
        }
        console.log(array);
    };

    return (
        <div>
            <h1>Ratings</h1>
        </div>
    );
};

export default Ratings;
